/**
 * Sets a constant default value for the property when it is undefined.
 * @template T
 * @template {keyof T***REMOVED*** Property
 * @param {T***REMOVED*** object An object.
 * @param {Property***REMOVED*** property A property of the provided object.
 * @param {T[Property]***REMOVED*** [defaultValue] The default value to set for the property.
 * @returns {T[Property]***REMOVED*** The defaulted property value.
 */
export function d<T, Property extends keyof T>(
  object: T,
  property: Property,
  defaultValue?: T[Property] | undefined
): T[Property];
/**
 * Resolves the value for a nested object option.
 * @template T
 * @template {keyof T***REMOVED*** Property
 * @template Result
 * @param {T***REMOVED*** object An object.
 * @param {Property***REMOVED*** property A property of the provided object.
 * @param {function(T | undefined): Result***REMOVED*** fn The handler to resolve the property's value.
 * @returns {Result***REMOVED*** The resolved option value.
 */
export function n<T, Property extends keyof T, Result>(
  object: T,
  property: Property,
  fn: (arg0: T | undefined) => Result
): Result;
