#!/usr/bin/env bash
###-begin-{{cmd***REMOVED******REMOVED***-completion-###
#
# {{cmd***REMOVED******REMOVED*** command completion script
#
# Installation: {{cmd***REMOVED******REMOVED*** completion >> ~/.bashrc  (or ~/.zshrc)
# Or, maybe: {{cmd***REMOVED******REMOVED*** completion > /usr/local/etc/bash_completion.d/{{cmd***REMOVED******REMOVED***
#

COMP_WORDBREAKS=${COMP_WORDBREAKS/=/***REMOVED***
COMP_WORDBREAKS=${COMP_WORDBREAKS/@/***REMOVED***
export COMP_WORDBREAKS

if complete &>/dev/null; then
  _{{cmd***REMOVED******REMOVED***_completion () {
    local si="$IFS"
    IFS=$'\n' COMPREPLY=($(COMP_CWORD="$COMP_CWORD" \
                           COMP_LINE="$COMP_LINE" \
                           COMP_POINT="$COMP_POINT" \
                           {{cmd***REMOVED******REMOVED*** completion -- "${COMP_WORDS[@]***REMOVED***" \
                           2>/dev/null)) || return $?
    IFS="$si"
  ***REMOVED***
  complete -F _{{cmd***REMOVED******REMOVED***_completion {{cmd***REMOVED******REMOVED***
elif compctl &>/dev/null; then
  _{{cmd***REMOVED******REMOVED***_completion () {
    local cword line point words si
    read -Ac words
    read -cn cword
    let cword-=1
    read -l line
    read -ln point
    si="$IFS"
    IFS=$'\n' reply=($(COMP_CWORD="$cword" \
                       COMP_LINE="$line" \
                       COMP_POINT="$point" \
                       {{cmd***REMOVED******REMOVED*** completion -- "${words[@]***REMOVED***" \
                       2>/dev/null)) || return $?
    IFS="$si"
  ***REMOVED***
  compctl -K _{{cmd***REMOVED******REMOVED***_completion {{cmd***REMOVED******REMOVED***
fi
###-end-{{cmd***REMOVED******REMOVED***-completion-###
