/**
Check if a value is a plain object.

An object is plain if it's created by either `{***REMOVED***`, `new Object()`, or `Object.create(null)`.

@example
```
import isPlainObject = require('is-plain-obj');

isPlainObject({foo: 'bar'***REMOVED***);
//=> true

isPlainObject(new Object());
//=> true

isPlainObject(Object.create(null));
//=> true

isPlainObject([1, 2, 3]);
//=> false

class Unicorn {***REMOVED***
isPlainObject(new Unicorn());
//=> false
```
*/
declare function isPlainObject<Value = unknown>(value: unknown): value is Record<string | number | symbol, Value>;

export = isPlainObject;
