export const completionShTemplate = `###-begin-{{app_name***REMOVED******REMOVED***-completions-###
#
# yargs command completion script
#
# Installation: {{app_path***REMOVED******REMOVED*** {{completion_command***REMOVED******REMOVED*** >> ~/.bashrc
#    or {{app_path***REMOVED******REMOVED*** {{completion_command***REMOVED******REMOVED*** >> ~/.bash_profile on OSX.
#
_yargs_completions()
{
    local cur_word args type_list

    cur_word="\${COMP_WORDS[COMP_CWORD]***REMOVED***"
    args=("\${COMP_WORDS[@]***REMOVED***")

    # ask yargs to generate completions.
    type_list=$({{app_path***REMOVED******REMOVED*** --get-yargs-completions "\${args[@]***REMOVED***")

    COMPREPLY=( $(compgen -W "\${type_list***REMOVED***" -- \${cur_word***REMOVED***) )

    # if no match was found, fall back to filename completion
    if [ \${#COMPREPLY[@]***REMOVED*** -eq 0 ]; then
      COMPREPLY=()
    fi

    return 0
***REMOVED***
complete -o default -F _yargs_completions {{app_name***REMOVED******REMOVED***
###-end-{{app_name***REMOVED******REMOVED***-completions-###
`;
export const completionZshTemplate = `###-begin-{{app_name***REMOVED******REMOVED***-completions-###
#
# yargs command completion script
#
# Installation: {{app_path***REMOVED******REMOVED*** {{completion_command***REMOVED******REMOVED*** >> ~/.zshrc
#    or {{app_path***REMOVED******REMOVED*** {{completion_command***REMOVED******REMOVED*** >> ~/.zsh_profile on OSX.
#
_{{app_name***REMOVED******REMOVED***_yargs_completions()
{
  local reply
  local si=$IFS
  IFS=$'\n' reply=($(COMP_CWORD="$((CURRENT-1))" COMP_LINE="$BUFFER" COMP_POINT="$CURSOR" {{app_path***REMOVED******REMOVED*** --get-yargs-completions "\${words[@]***REMOVED***"))
  IFS=$si
  _describe 'values' reply
***REMOVED***
compdef _{{app_name***REMOVED******REMOVED***_yargs_completions {{app_name***REMOVED******REMOVED***
###-end-{{app_name***REMOVED******REMOVED***-completions-###
`;
